mapper.select:
SELECT o.*, rc.name AS rc_name, rcc.name AS rc_contact_name, p.name AS project_name, g.no AS goods_no, g.spec_x AS goods_spec_x, g.spec_y AS goods_spec_y, g.photos AS goods_photos, g.name AS goods_name, g.unit AS goods_unit, gt.name AS type_name, u.name AS create_name FROM t_order o LEFT JOIN t_related_company rc ON o.rc_id = rc.id LEFT JOIN t_related_company_contact rcc ON o.rc_contact_id = rcc.id LEFT JOIN t_project p ON o.project_id = p.id LEFT JOIN t_goods g ON o.goods_id = g.id LEFT JOIN t_goods_type gt ON g.type_id = gt.id LEFT JOIN t_user u on o.create_id = u.id WHERE o.enterprise_id = #{enterpriseId} AND o.delete_status = #{deleteStatus} AND o.type in
#{type}
ORDER BY o.modify_time DESC

SELECT o.*
FROM t_order o
LEFT JOIN t_goods g ON o.goods_id = g.id
LEFT JOIN t_goods_type gt ON g.type_id = gt.id
WHERE o.parent_id = #{parentId}
AND gt.symbol = #{symbol}

mapper.delete:
UPDATE t_order
SET delete_status = '1'
WHERE id = #{id}
